document.addEventListener('turbolinks:load', () => {
  alert("test")
});

let menulist = document.getElementsByClassName("account-menu");
let display_btn = document.getElementsByClassName("profile-image");

display_btn.addEventListener("click",()=>{
  menulist.classList.toggle("hide");
});
